import i18n from 'i18next';

const loadComponentNamespace = async (componentName) => {
    const namespace = componentName;

    console.log(`Loading namespace ${namespace} for language ${i18n.language}`);

    if (!i18n.hasResourceBundle(i18n.language, namespace)) {
        console.log(`Namespace ${namespace} not loaded. Loading...`);
        await i18n.loadNamespaces(namespace);

        // Check if the namespace is now available
        const bundleExistsAfter = i18n.hasResourceBundle(i18n.language, namespace);
        console.log(`Namespace ${namespace} loaded and available: ${bundleExistsAfter}`);

        // Log the current resources for this language
        console.log('Current i18n resources:', i18n.getDataByLanguage(i18n.language));

        // Manually inspect the available translations
        if (i18n.getDataByLanguage(i18n.language) && i18n.getDataByLanguage(i18n.language)[namespace]) {
            console.log(`Loaded translations for ${namespace}:`, i18n.getDataByLanguage(i18n.language)[namespace]);
        } else {
            console.error(`No translations found for ${namespace} in ${i18n.language}`);
        }
    } else {
        console.log(`Namespace ${namespace} already loaded.`);
    }
};


export const TranslationUtils = {
    loadComponentNamespace: loadComponentNamespace
}
