import React, {Suspense, useEffect} from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import DetailsHeader from './components/DetailsHeader/DetailsHeader';
import SectionHeader from './components/SectionHeader/SectionHeader';
import {TextField} from '../../../../components/TextField/TextField';
import {VerticalContainer} from '../../../../styles/commonStyles';
import useDynamicTranslation from "../../../../hooks/translation/useDynamicTranslation";

const WidgetContainer = styled(VerticalContainer)`
  min-height: 100%;
  background-color: white;
  box-shadow: 0px 1px 4px 1px #888888;
`;

const ContentContainer = styled.div`
  display: flex;
  padding-top: 15px;
  flex: 1;
`;

const SectionContainer = styled.div`
    width: 100%;
    padding: 0 12px 5px;
    min-height: 100%;
`;

const FieldsContainer = styled.div`
    margin-top: 6px;
`;

export default function DetailsView({resource}) {
    const { t, i18n } = useDynamicTranslation('DetailsView');

    const {name, description, resourceType, path} = resource;

    const fields = [
        {
            value: name,
            label: t('FIELD_TITLE_NAME')
        },
        {
            value: description,
            label: t('FIELD_TITLE_DESCRIPTION')
        },
        {
            value: resourceType,
            label: t('FIELD_TITLE_RESOURCE_TYPE')
        },
        {
            value: path,
            label: t('FIELD_TITLE_RESOURCE_PATH')
        }
    ];

    useEffect(() => {
        // Load the namespace dynamically if needed
        i18n.loadNamespaces('DetailsView').then(() => {
            console.log('DetailsView translations loaded');
        });
    }, [i18n]);

    return (
        <Suspense fallback={<div>Loading...</div>}>
        <WidgetContainer>
            <DetailsHeader {...{name}} />
            <ContentContainer>
                <SectionContainer>
                    <SectionHeader {...{
                        headerText: t('TITLE')
                    }} />
                    <FieldsContainer>
                        {fields.map((props) => <TextField key={props.value} {...props}/>)}
                    </FieldsContainer>
                </SectionContainer>
            </ContentContainer>
        </WidgetContainer>
        </Suspense>
    );
}

DetailsView.propTypes = {
    resource: PropTypes.object
};
