import React from 'react';
import styled from 'styled-components';
import logo from '../../assets/images/plainid-logo-white.png';
import {headerPanelHeight} from '../../styles/commonStyles';
import {useTranslation} from "react-i18next";

const HeaderPanelContainer = styled.header`
  display: flex;
  background-color: #4b555f;
  top: 0;
  left: 0;
  right: 0;
  position: fixed;
  height: ${headerPanelHeight};
  align-items: center;
  z-index: 1;
  justify-content: space-between;
  padding: 0 20px; 
`;

const Logo = styled.img`
  width: auto;
  height: 100%;
  padding: 7px;
`;

const LanguageSelect = styled.select`
  background: transparent;
  color: white;
  border: 1px solid white;
  font-size: 16px;
  cursor: pointer;
  padding: 7px;
  border-radius: 4px;

  option {
    color: black; /* Ensures the options are visible */
  }

  &:hover {
    background-color: rgba(255, 255, 255, 0.1);
  }
`;

export default function HeaderPanel() {
    const { i18n } = useTranslation();


    const toggleLanguage = () => {
        const currentLang = i18n.language;
        const newLang = currentLang === 'en-US' ? 'es-ES' : 'en-US';
        i18n.changeLanguage(newLang);
    };
    return (
        <HeaderPanelContainer>
            <Logo {...{
                src: logo,
                alt: 'Logo'
            }} />
            <LanguageSwitcher onClick={toggleLanguage}>
                {i18n.language === 'en-US' ? 'Switch to Spanish' : 'Switch to English'}
            </LanguageSwitcher>
        </HeaderPanelContainer>
    );
}
