import i18n from 'i18next';

export const loadComponentNamespace = async (componentName) => {
    const namespace = componentName;
    console.log(`Attempting to load namespace: ${namespace} for language: ${i18n.language}`);

    // Check if the resource bundle is already loaded
    const hasResourceBundle = i18n.hasResourceBundle(i18n.language, namespace);
    console.log(`Namespace ${namespace} already loaded: ${hasResourceBundle}`);

    if (!hasResourceBundle) {
        try {
            await i18n.loadNamespaces(namespace);
            console.log(`Namespace ${namespace} loaded successfully`);
        } catch (error) {
            console.error(`Failed to load namespace ${namespace}:`, error);
        }
    }
};

export const TranslationUtils = {
    loadComponentNamespace: loadComponentNamespace
}
