import i18n from "i18next";
import HttpBackend from "i18next-http-backend/cjs";
import {initReactI18next} from "react-i18next";

const dynamicBackend = async (lng, ns) => {
    try {
        const translation = await import(`./containers/ResourcesContainer/components/${ns}/locales/${lng}/strings.json`);
        return translation;
    } catch (error) {
        console.error(`Error loading translation for namespace: ${ns}, language: ${lng}`, error);
        return {}; // Fallback if not found
    }
};

i18n
    .use(HttpBackend)
    .use(initReactI18next)
    .init({
        fallbackLng: 'en-US',
        supportedLngs: ['en-US', 'es-ES'],
        defaultNS: 'DetailsView',
        ns: ['DetailsView'],
        backend: {
            // loadPath: '/containers/ResourcesContainer/components/{{ns}}/locales/{{lng}}/strings.json'
            dynamicBackend
        },
        react: {
            useSuspense: true
        },
        debug: true, // Enable debug mode for more detailed logs
    })

export default i18n;
